  z-index: 1000;
}

.centered-modal {
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 400px;
  height: 300px;
}

.container {
  width: 300px;
  padding: 20px;
  border: 2px solid #ccc;
  box-sizing: border-box;
}

.center-absolute {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.fade-in {
  animation: fadeIn 0.5s ease-in-out;
}

@keyframes slideInFromLeft {
  0% {
    transform: translateX(-100%);
    opacity: 0;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
}

.slide-in {
  animation: slideInFromLeft 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

@keyframes bounce {
  0%, 20%, 50%, 80%, 100% {
    transform: translateY(0);
  }
  40% {
    transform: translateY(-30px);
  }
  60% {
    transform: translateY(-15px);
  }
}

.bounce {
  animation: bounce 2s infinite;
}

@keyframes pulse {
  0% {
    transform: scale(1);
    box-shadow: 0 0 0 0 rgba(59, 130, 246, 0.7);
  }
  70% {
    transform: scale(1.05);
    box-shadow: 0 0 0 10px rgba(59, 130, 246, 0);
  }
  100% {
    transform: scale(1);
    box-shadow: 0 0 0 0 rgba(59, 130, 246, 0);
  }
}

.pulse-button {
  animation: pulse 2s infinite;
}

@keyframes rotate {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

.loading-spinner {
  animation: rotate 1s linear infinite;
}

@keyframes shake {
  0%, 100% { transform: translateX(0); }
  10%, 30%, 50%, 70%, 90% { transform: translateX(-10px); }
  20%, 40%, 60%, 80% { transform: translateX(10px); }
}

.error-shake {
  animation: shake 0.82s cubic-bezier(0.36, 0.07, 0.19, 0.97) both;
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-20px);
  }
}

.floating-element {
  animation: float 3s ease-in-out infinite;
  animation-delay: 0.5s;
  animation-fill-mode: both;
}

@keyframes flipIn {
  0% {
    transform: perspective(400px) rotateY(-90deg);
    opacity: 0;
  }
  40% {
    transform: perspective(400px) rotateY(-10deg);
  }
  70% {
    transform: perspective(400px) rotateY(10deg);
  }
  100% {
    transform: perspective(400px) rotateY(0deg);
    opacity: 1;
  }
}

.flip-in {
  animation: flipIn 0.6s ease-in-out;
}

.multi-animation {
  animation: 
    fadeIn 0.5s ease-out,
    slideInFromLeft 0.5s ease-out,
    pulse 2s infinite 0.5s;
}

@keyframes slideIn {
  0% {
    transform: translateX(-100%);
    opacity: 0;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
}

/* 基本的な使い方 */
.hidden-element {
  visibility: hidden;
}

.visible-element {
  visibility: visible;
}

/* display: noneとの比較 */
.visibility-hidden {
  visibility: hidden; /* 領域は確保される */
}

.display-none {
  display: none; /* 領域も削除される */
}

/* スムーズなフェードイン・アウト */
.fade-element {
  visibility: hidden;
  opacity: 0;
  transition: opacity 0.3s ease, visibility 0.3s ease;
}

.fade-element.show {
  visibility: visible;
  opacity: 1;
}

/* ツールチップの実装 */
.tooltip {
  position: absolute;
  background: #333;
  color: white;
  padding: 8px 12px;
  border-radius: 4px;
  visibility: hidden;
  opacity: 0;
  transition: all 0.2s;
  pointer-events: none;
}

.tooltip-trigger:hover .tooltip {
  visibility: visible;
  opacity: 1;
  pointer-events: auto;
}

/* レスポンシブでの表示制御 */
.desktop-only {
  visibility: visible;
}

@media (max-width: 768px) {
  .desktop-only {
    visibility: hidden;
  }
}

/* JavaScript連携の例 */
.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  visibility: hidden;
  opacity: 0;
  transition: all 0.3s;
}

.modal-overlay.active {
  visibility: visible;
  opacity: 1;
}

.truncate {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.sidebar-title {
  writing-mode: vertical-rl;
  text-orientation: mixed;
  padding: 1rem;
  background: #f3f4f6;
}

.book-spine {
  writing-mode: vertical-rl;
  text-orientation: sideways;
  height: 300px;
  width: 40px;
  background: #1f2937;
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
}

/* アニメーション終了後 */
.element {
  will-change: auto;
}

@media print {
  .no-print {
    display: none;
  }
  body {
    font-size: 12pt;
    color: black;
  }
}

.table-auto {
  table-layout: auto;
  width: 100%;
}

/* セル内容に応じて幅が自動調整される */
th, td {
  padding: 8px;
  text-align: left;
}

.table-fixed {
  table-layout: fixed;
  width: 100%;
}

/* 最初の行の幅設定が全体に適用される */
th:nth-child(1) { width: 30%; }
th:nth-child(2) { width: 50%; }
th:nth-child(3) { width: 20%; }

.performance-table {
  table-layout: fixed;
  width: 100%;
}

/* 大量データのテーブルでパフォーマンス向上 */
.col-id { width: 10%; }
.col-name { width: 40%; }
.col-email { width: 35%; }
.col-status { width: 15%; }

.responsive-table {
  table-layout: fixed;
  width: 100%;
}

/* レスポンシブテーブルの基本設定 */
@media (max-width: 768px) {
  .responsive-table {
    table-layout: auto;
  }
}

.data-table {
  table-layout: fixed;
  border-collapse: collapse;
  width: 100%;
}

/* 均等幅のカラムレイアウト */
.data-table th,
.data-table td {
  width: 25%;
  padding: 12px;
  border: 1px solid #ddd;
}
